import React from 'react';

import * as classes from './timeinput.module.css'

const TimeInput = props => {

    let handleTimeChange = (event) => {
        let time = event.target.value;
        let timeSplit = time.split(":");
        let hours = timeSplit[0];
        let minutes = timeSplit[1];
        let timePeriod = "AM";

        if (hours > 12) {
            hours -= 12;
            timePeriod = "PM"
        }

        props.changedTime(hours, "hours")
        props.changedTime(minutes, "minutes")
        props.changedtimePeriod(timePeriod)
    }


    let parseAndSetTime = () => {

        let { hours, minutes, timePeriod } = props;

        if (timePeriod === "PM")
            hours = "" + parseInt(parseInt(hours) + 12)

        return `${hours}:${minutes}`
    }

    console.log("iIN asd asdsa")
    return (



        <div className={classes.Container}>
            <p>Select Time</p>
            {/* <input
                type="number"
                placeholder="HH"
                className={classes.HourDisplay}
                onChange={(event) => props.changedTime(event, "hours")}
                value={props.hours}
            />
            <input
                type="number"
                placeholder="MM"
                className={classes.HourDisplay}
                onChange={(event) => props.changedTime(event, "minutes")}
                value={props.minutes}
            />
            <select className={classes.AM} value={props.timePeriod} onChange={props.changedtimePeriod}>

                <option value="AM">AM</option>
                <option value="PM">PM</option>
            </select> */}

            <input
                type="time"
                min="07:00"
                max="23:00"
                onChange={handleTimeChange}
                value={parseAndSetTime()}
            />

        </div >

    )
}

export default TimeInput;
